package com.weitu.songda.entity;import java.io.Serializable;import com.weitu.framework.core.annotation.RsEntity;import com.weitu.framework.component.orm.annotation.RdColumn;import com.weitu.framework.component.orm.annotation.RdTable;import com.weitu.framework.component.orm.annotation.RdId;//@RsEntity@RdTable(name = "ROSE")public class Rose implements Serializable{    private static final long serialVersionUID = 1L;    // (NULL)    public static final String T_NAME = "NAME";    //TITLE;TTT (NOT NULL)    public static final String T_ID = "ID";    @RdColumn(name = T_NAME)    private String name;    @RdId    @RdColumn(name = T_ID, unique = true)    private String id;    public String getName(){        return this.name;    }    public void setName(String name){        this.name = name;    }    public String getId(){        return this.id;    }    public void setId(String id){        this.id = id;    }}