package com.weitu.songda.entity;import java.util.Date;import java.io.Serializable;import com.weitu.framework.core.annotation.RsEntity;import com.weitu.framework.component.orm.annotation.RdColumn;import com.weitu.framework.component.orm.annotation.RdTable;import com.weitu.framework.component.orm.annotation.RdId;//@RsEntity@RdTable(name = "T_TEST")public class Test implements Serializable{    private static final long serialVersionUID = 1L;    //金额;金额 (NULL)    public static final String T_BALANCE = "BALANCE";    //名称;姓名 (NULL)    public static final String T_NAME = "NAME";    //内容;内容 (NULL)    public static final String T_CONTENT = "CONTENT";    //编号;自增数字 (NOT NULL)    public static final String T_ID = "ID";    //创建日期;创建时间 (NULL)    public static final String T_CREATE_DATE = "CREATE_DATE";    //类型;类型 (NULL)    public static final String T_TYPE = "TYPE";    private String typeName;    @RdColumn(name = T_BALANCE)    private Double balance;    private Double minBalance;    private Double maxBalance;    @RdColumn(name = T_NAME)    private String name;    @RdColumn(name = T_CONTENT)    private String content;    @RdId    @RdColumn(name = T_ID, unique = true)    private Integer id;    @RdColumn(name = T_CREATE_DATE)    private Date createDate;    private Date startCreateDate;    private Date endCreateDate;    @RdColumn(name = T_TYPE)    private Integer type;    private Integer minType;    private Integer maxType;    public Double getBalance(){        return this.balance;    }    public void setBalance(Double balance){        this.balance = balance;    }    public Double getMinBalance(){        return this.minBalance;    }    public void setMinBalance(Double minBalance){        this.minBalance = minBalance;    }    public Double getMaxBalance(){        return this.maxBalance;    }    public void setMaxBalance(Double maxBalance){        this.maxBalance = maxBalance;    }    public String getName(){        return this.name;    }    public void setName(String name){        this.name = name;    }    public String getContent(){        return this.content;    }    public void setContent(String content){        this.content = content;    }    public Integer getId(){        return this.id;    }    public void setId(Integer id){        this.id = id;    }    public Date getCreateDate(){        return this.createDate;    }    public void setCreateDate(Date createDate){        this.createDate = createDate;    }    public Date getStartCreateDate(){        return this.startCreateDate;    }    public void setStartCreateDate(Date startCreateDate){        this.startCreateDate = startCreateDate;    }    public Date getEndCreateDate(){        return this.endCreateDate;    }    public void setEndCreateDate(Date endCreateDate){        this.endCreateDate = endCreateDate;    }    public Integer getType(){        return this.type;    }    public void setType(Integer type){        this.type = type;    }    public Integer getMinType(){        return this.minType;    }    public void setMinType(Integer minType){        this.minType = minType;    }    public Integer getMaxType(){        return this.maxType;    }    public void setMaxType(Integer maxType){        this.maxType = maxType;    }}