package com.weitu.songda.entity;import java.io.Serializable;import com.weitu.framework.core.annotation.RsEntity;import com.weitu.framework.component.orm.annotation.RdColumn;import com.weitu.framework.component.orm.annotation.RdTable;import com.weitu.framework.component.orm.annotation.RdId;//@RsEntity@RdTable(name = "T_TEST_TYPE")public class TestType implements Serializable{    private static final long serialVersionUID = 1L;    //名称;姓名 (NULL)    public static final String T_NAME = "NAME";    //编号;自增数字 (NOT NULL)    public static final String T_ID = "ID";    @RdColumn(name = T_NAME)    private String name;    @RdId    @RdColumn(name = T_ID, unique = true)    private Integer id;    public String getName(){        return this.name;    }    public void setName(String name){        this.name = name;    }    public Integer getId(){        return this.id;    }    public void setId(Integer id){        this.id = id;    }}